import React from'react'
import'./App.css';

function App(){
 const[todos,setTodos]=React.useState([])
 const[todo,setTodo]=React.useState("")
 const[todoEditing, setTodoEditing]= React.useState(null)
 const[editingText, setEditingText]= React.useState("")




 function handleSubmit(e){

   e.preventDefault()
   const newTodo={
     id:new Date().getTime(),
      text:todo,
      completed:false,
   }
   setTodos([...todos].concat(newTodo))
   setTodo("")
  }

  function deleteTodo(id){
    const updatedTodos=[...todos].filter((todo) => todo.id!==id)
    setTodos(updatedTodos)
  }

  function toggleComplete(id){
    const updatedTodos = [...todos].filter((todo)=>{
      if(todo.id===id){
        todo.completed = !todo.completed
      }
      return todo
    })
    setTodos(updatedTodos)
  }

  function editTodo (id){
    const updatedTodos = [...todos].map((todo)=>{
      if(todo.id ===id){
        todo.text = editingText
      }
      return todo
    })
    setTodos(updatedTodos)
    setTodoEditing(null)
    setEditingText("")
  }

return(
  <div className="App">

    {todos.map((todo)=><div key={todo.id}>

    <div class="flex space-x-4 ml-10">

      <input 
          className='space-x-5 '
          type="checkbox"   
          onChange={()=>toggleComplete(todo.id)}
          checked={todo.completed}/>
      <span>{todo.text}</span>
      <button onClick={() => deleteTodo(todo.id)}>&#10060;</button>



    </div>




  </div>)}

      <div className="fixed bottom-2 grid w-screen place-items-center">

        <form onSubmit={handleSubmit}>

        <div>
          <input class="block rounded-t-lg px-2.5 pb-2.5 pt-5 w-full text-sm text-gray-900 bg-gray-50 dark:bg-gray-700 border-0 border-b-2 border-gray-300 appearance-none dark:text-white dark:border-gray-600 dark:focus:border-blue-500 focus:outline-none focus:ring-0 focus:border-blue-600 mb-1 peer" placeholder="Add new Todo"   type="text"onChange={(e)=>setTodo(e.target.value)}value={todo}/>
        </div>

        <div>
          <button className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded-full" type="submit">Add Todo</button>
        </div>

        </form>
      </div>



  </div>
);
}
export default App;
